import React, { useState, useRef, useEffect } from 'react';
import { Send, Bot, User, FileText } from 'lucide-react';
import { chat } from '../services/api.ts';
import { ChatMessage, Source } from '../types/index.ts';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';

const ChatInterface: React.FC = () => {
    const [messages, setMessages] = useState<ChatMessage[]>([]);
    const [inputMessage, setInputMessage] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const messagesEndRef = useRef<HTMLDivElement>(null);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    const handleSendMessage = async () => {
        if (!inputMessage.trim() || isLoading) return;

        const userMessage: ChatMessage = {
            id: Date.now().toString(),
            content: inputMessage,
            type: 'user',
            timestamp: new Date().toISOString()
        };

        setMessages(prev => [...prev, userMessage]);
        setInputMessage('');
        setIsLoading(true);

        try {
            const response = await chat(inputMessage);

            const assistantMessage: ChatMessage = {
                id: (Date.now() + 1).toString(),
                content: response.response,
                type: 'assistant',
                timestamp: new Date().toISOString(),
                sources: response.sources
            };

            setMessages(prev => [...prev, assistantMessage]);
        } catch (error) {
            console.error('Chat error:', error);
            const errorMessage: ChatMessage = {
                id: (Date.now() + 1).toString(),
                content: 'Sorry, I encountered an error. Please try again.',
                type: 'assistant',
                timestamp: new Date().toISOString()
            };
            setMessages(prev => [...prev, errorMessage]);
        } finally {
            setIsLoading(false);
        }
    };

    const handleKeyPress = (e: React.KeyboardEvent) => {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault();
            handleSendMessage();
        }
    };

    const renderMessage = (message: ChatMessage) => (
        <div key={message.id} className={`message ${message.type}`}>
            <div className="message-avatar">
                {message.type === 'user' ? <User size={20} /> : <Bot size={20} />}
            </div>
            <div className="message-content">
                <div className="message-text">
                    <ReactMarkdown remarkPlugins={[remarkGfm]}>
                        {message.content}
                    </ReactMarkdown>
                </div>
                {message.sources && message.sources.length > 0 && (
                    <div className="message-sources">
                        <h4>Sources:</h4>
                        {message.sources.map((source: Source, index: number) => (
                            <div key={index} className="source-item">
                                <FileText size={16} />
                                <div className="source-content">
                                    <p>{source.content.substring(0, 200)}...</p>
                                    {source.metadata.source && (
                                        <small>Source: {source.metadata.source}</small>
                                    )}
                                </div>
                            </div>
                        ))}
                    </div>
                )}
                <div className="message-timestamp">
                    {new Date(message.timestamp).toLocaleTimeString()}
                </div>
            </div>
        </div>
    );

    return (
        <div className="chat-interface">
            <div className="chat-header">
                <h2>RAG Chatbot</h2>
                <p>Ask questions about your uploaded documents</p>
            </div>

            <div className="chat-messages">
                {messages.length === 0 ? (
                    <div className="welcome-message">
                        <Bot size={48} />
                        <h3>Welcome to RAG Chatbot!</h3>
                        <p>Start a conversation by typing a message below. I'll search through your uploaded documents to provide relevant answers.</p>
                    </div>
                ) : (
                    messages.map(renderMessage)
                )}
                {isLoading && (
                    <div className="message assistant">
                        <div className="message-avatar">
                            <Bot size={20} />
                        </div>
                        <div className="message-content">
                            <div className="typing-indicator">
                                <span></span>
                                <span></span>
                                <span></span>
                            </div>
                        </div>
                    </div>
                )}
                <div ref={messagesEndRef} />
            </div>

            <div className="chat-input">
                <textarea
                    value={inputMessage}
                    onChange={(e) => setInputMessage(e.target.value)}
                    onKeyPress={handleKeyPress}
                    placeholder="Type your message here..."
                    disabled={isLoading}
                    rows={1}
                />
                <button
                    onClick={handleSendMessage}
                    disabled={!inputMessage.trim() || isLoading}
                    className="send-button"
                >
                    <Send size={20} />
                </button>
            </div>
        </div>
    );
};

export default ChatInterface;
