import React, { useState, useEffect } from 'react';
import Sidebar from './components/Sidebar';
import ChatInterface from './components/ChatInterface';
import DocumentUpload from './components/DocumentUpload';
import { getStats } from './services/api';
import './App.css';

type TabType = 'chat' | 'upload' | 'stats';

interface Stats {
    totalDocuments: number;
    vectorStoreStatus: string;
}

function App() {
    const [activeTab, setActiveTab] = useState<TabType>('chat');
    const [stats, setStats] = useState<Stats>({
        totalDocuments: 0,
        vectorStoreStatus: 'inactive'
    });

    useEffect(() => {
        loadStats();
    }, []);

    const loadStats = async () => {
        try {
            const statsData = await getStats();
            setStats(statsData);
        } catch (error) {
            console.error('Failed to load stats:', error);
        }
    };

    const handleTabChange = (tab: TabType) => {
        setActiveTab(tab);
    };

    const handleDocumentUploaded = () => {
        loadStats(); // Refresh stats after upload
    };

    const renderContent = () => {
        switch (activeTab) {
            case 'chat':
                return <ChatInterface />;
            case 'upload':
                return <DocumentUpload onUploaded={handleDocumentUploaded} />;
            case 'stats':
                return (
                    <div className="stats-container">
                        <h2>Knowledge Base Statistics</h2>
                        <div className="stats-grid">
                            <div className="stat-card">
                                <h3>Total Documents</h3>
                                <p className="stat-number">{stats.totalDocuments}</p>
                            </div>
                            <div className="stat-card">
                                <h3>Vector Store Status</h3>
                                <p className={`stat-status ${stats.vectorStoreStatus}`}>
                                    {stats.vectorStoreStatus}
                                </p>
                            </div>
                        </div>
                    </div>
                );
            default:
                return <ChatInterface />;
        }
    };

    return (
        <div className="app">
            <Sidebar
                activeTab={activeTab}
                onTabChange={handleTabChange}
                stats={stats}
            />
            <main className="main-content">
                {renderContent()}
            </main>
        </div>
    );
}

export default App;
