import React, { useState, useRef, useEffect } from 'react';
import { Send, Bot, User, FileText } from 'lucide-react';
import { chat } from '../services/api.js';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';

const ChatInterface = ({ persona }) => {
    const [messages, setMessages] = useState([]);
    const [inputMessage, setInputMessage] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const messagesEndRef = useRef(null);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    const handleSendMessage = async () => {
        if (!inputMessage.trim() || isLoading) return;

        const userMessage = {
            id: Date.now().toString(),
            content: inputMessage,
            type: 'user',
            timestamp: new Date().toISOString()
        };

        setMessages(prev => [...prev, userMessage]);
        setInputMessage('');
        setIsLoading(true);

        try {
            const response = await chat(inputMessage);

            const assistantMessage = {
                id: (Date.now() + 1).toString(),
                content: response.response,
                type: 'assistant',
                timestamp: new Date().toISOString(),
                sources: response.sources
            };

            setMessages(prev => [...prev, assistantMessage]);
        } catch (error) {
            console.error('Chat error:', error);
            const errorMessage = {
                id: (Date.now() + 1).toString(),
                content: 'Sorry, I encountered an error. Please try again.',
                type: 'assistant',
                timestamp: new Date().toISOString()
            };
            setMessages(prev => [...prev, errorMessage]);
        } finally {
            setIsLoading(false);
        }
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault();
            handleSendMessage();
        }
    };

    const renderMessage = (message) => (
        <div key={message.id} className={`message ${message.type}`}>
            <div className="message-avatar">
                {message.type === 'user' ? <User size={20} /> : <Bot size={20} />}
            </div>
            <div className="message-content">
                <div className="message-text">
                    <ReactMarkdown remarkPlugins={[remarkGfm]}>
                        {message.content}
                    </ReactMarkdown>
                </div>
                {message.sources && message.sources.length > 0 && (
                    <div className="message-sources">
                        <h4>Sources from {persona.name}'s Knowledge Base:</h4>
                        {message.sources.map((source, index) => (
                            <div key={index} className="source-item">
                                <FileText size={16} />
                                <div className="source-content">
                                    <p>{source.content.substring(0, 200)}...</p>
                                    {source.metadata.source && (
                                        <small>Source: {source.metadata.source}</small>
                                    )}
                                </div>
                            </div>
                        ))}
                    </div>
                )}
                <div className="message-timestamp">
                    {new Date(message.timestamp).toLocaleTimeString()}
                </div>
            </div>
        </div>
    );

    return (
        <div className="chat-interface">
            <div className="chat-header">
                <h2>Chat with {persona.name}</h2>
                <p>{persona.role}</p>
                <p className="chat-description">
                    Ask questions and get responses as if you're talking directly to {persona.name}. 
                    The AI will respond based on {persona.name}'s interviews, articles, videos, and professional documents.
                </p>
            </div>

            <div className="chat-messages">
                {messages.length === 0 ? (
                    <div className="welcome-message">
                        <User size={48} />
                        <h3>Welcome! You're chatting with {persona.name}</h3>
                        <p>
                            This AI chatbot has been trained on {persona.name}'s knowledge base including interviews, 
                            articles, videos, and professional documents. Ask questions and get responses as if you're 
                            talking directly to {persona.name}.
                        </p>
                        <div className="persona-example">
                            <h4>Example questions you can ask:</h4>
                            <ul>
                                <li>"What's your experience with machine learning?"</li>
                                <li>"Tell me about your approach to AI development"</li>
                                <li>"What advice would you give to aspiring engineers?"</li>
                            </ul>
                        </div>
                    </div>
                ) : (
                    messages.map(renderMessage)
                )}
                {isLoading && (
                    <div className="message assistant">
                        <div className="message-avatar">
                            <Bot size={20} />
                        </div>
                        <div className="message-content">
                            <div className="typing-indicator">
                                <span></span>
                                <span></span>
                                <span></span>
                            </div>
                        </div>
                    </div>
                )}
                <div ref={messagesEndRef} />
            </div>

            <div className="chat-input">
                <textarea
                    value={inputMessage}
                    onChange={(e) => setInputMessage(e.target.value)}
                    onKeyPress={handleKeyPress}
                    placeholder={`Ask ${persona.name} a question...`}
                    disabled={isLoading}
                    rows={1}
                />
                <button
                    onClick={handleSendMessage}
                    disabled={!inputMessage.trim() || isLoading}
                    className="send-button"
                >
                    <Send size={20} />
                </button>
            </div>
        </div>
    );
};

export default ChatInterface;
