import React from 'react';
import { MessageCircle, Upload, BarChart3, Bot } from 'lucide-react';
import { DocumentStats } from '../types';

interface SidebarProps {
    activeTab: 'chat' | 'upload' | 'stats';
    onTabChange: (tab: 'chat' | 'upload' | 'stats') => void;
    stats: DocumentStats;
}

const Sidebar: React.FC<SidebarProps> = ({ activeTab, onTabChange, stats }) => {
    const tabs = [
        {
            id: 'chat' as const,
            label: 'Chat',
            icon: MessageCircle,
            description: 'Ask questions about your documents'
        },
        {
            id: 'upload' as const,
            label: 'Upload',
            icon: Upload,
            description: 'Add new documents to your knowledge base'
        },
        {
            id: 'stats' as const,
            label: 'Statistics',
            icon: BarChart3,
            description: 'View knowledge base statistics'
        }
    ];

    return (
        <aside className="sidebar">
            <div className="sidebar-header">
                <div className="logo">
                    <Bot size={32} />
                    <h1>RAG Chatbot</h1>
                </div>
                <p className="tagline">AI-powered document Q&A</p>
            </div>

            <nav className="sidebar-nav">
                <ul className="nav-list">
                    {tabs.map((tab) => {
                        const Icon = tab.icon;
                        const isActive = activeTab === tab.id;

                        return (
                            <li key={tab.id}>
                                <button
                                    className={`nav-item ${isActive ? 'active' : ''}`}
                                    onClick={() => onTabChange(tab.id)}
                                >
                                    <Icon size={20} />
                                    <div className="nav-content">
                                        <span className="nav-label">{tab.label}</span>
                                        <span className="nav-description">{tab.description}</span>
                                    </div>
                                </button>
                            </li>
                        );
                    })}
                </ul>
            </nav>

            <div className="sidebar-footer">
                <div className="knowledge-base-stats">
                    <h3>Knowledge Base</h3>
                    <div className="stat-item">
                        <span className="stat-label">Documents</span>
                        <span className="stat-value">{stats.totalDocuments}</span>
                    </div>
                    <div className="stat-item">
                        <span className="stat-label">Status</span>
                        <span className={`stat-status ${stats.vectorStoreStatus}`}>
                            {stats.vectorStoreStatus}
                        </span>
                    </div>
                </div>
            </div>
        </aside>
    );
};

export default Sidebar;
